import { withRouter, useParams } from "react-router-dom";

function ProductDetail({ data }) {
  const { id } = useParams();

  return (
    <div>
      {data
        .filter((product) => product.id == id)
        .map((product, i) => (
          <div key={i}>
            <h1>{product.id}</h1>
            <h1>{product.name}</h1>
            <h1>{product.description}</h1>
            <img src={product.img} />
          </div>
        ))} 
    </div>
  );
}

export default withRouter(ProductDetail);
