import React from "react";
import makeStyles from "@mui/styles/makeStyles";
import {
  Grid,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  TextField,
} from "@mui/material";
import news from "./img/news.svg";
import { useState } from "react";

const useStyles = makeStyles((theme) => ({
  main: {
    position: "relative",
    marginTop: "-150px",
    margin: "auto",
    width: "100%",
    maxWidth: "1600px",
    height: "450px",
  },
  offer: { background: "#F4F5F7" },
  catalog: { background: "#FBEFEF" },
  title: {
    lineHeight: "50px",
    fontSize: "40px",
    marginLeft: "70px",
    marginTop: "100px",
  },
  body: {
    color: "#000",
    fontSize: "22px",
    maxWidth: "530px",
    marginLeft: "70px",
    marginTop: "10px",
  },
  button: {
    color: "#fff",
    width: "170px",
    height: "48px",
    left: "70px",
    marginTop: "15px",
  },
  img: { position: "absolute", bottom: "00px", right: "100px" },
}));

function SimpleDialog(props) {
  const { onClose, selectedValue, open } = props;

  const handleClose = () => {
    onClose(selectedValue);
  };

  const handleListItemClick = (value) => {
    onClose(value);
  };

  return (
    <Dialog onClose={handleClose} open={open}>
      <DialogTitle>Teklif Formu</DialogTitle>
      <List sx={{ pt: 0 }}>
        <ListItem>
          <TextField required label='Ad Soyad' />
        </ListItem>
        <ListItem>
          <TextField required label='Firma Adi' />
        </ListItem>
        <ListItem>
          <TextField required label='5xx xxx xxxx' />
        </ListItem>

        <ListItem
          autoFocus
          button
          onClick={() => handleListItemClick("addAccount")}
        >
          <ListItemAvatar>
            <Avatar>kjhkjkl </Avatar>
          </ListItemAvatar>
          <ListItemText primary="Add account" />
        </ListItem>
      </List>
    </Dialog>
  );
}

const emails = ["username@gmail.com", "user02@gmail.com"];

export default function OfferDownload() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [selectedValue, setSelectedValue] = React.useState(emails[1]);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = (value) => {
    setOpen(false);
    setSelectedValue(value);
  };

  return (
    <Grid container spacing={0} direction="row" className={classes.main}>
      <Grid
        container
        item
        xs={6}
        spacing={0}
        direction="row"
        className={classes.offer}
      >
        <Grid container item direction="column">
          <Typography variant="subtitle2" className={classes.title}>
            Teklif Alın
          </Typography>
          <Typography variant="body1" className={classes.body}>
            İleri teknolojimiz ve yetişmiş insan gücümüzle, 4500’den fazla
            ayakkabı modelimiz için teklif alın
          </Typography>
          <Button
            className={classes.button}
            disableRipple={true}
            style={{ backgroundColor: "#D50000" }}
            onClick={handleClickOpen}
          >
            Teklif Al
          </Button>
        </Grid>
      </Grid>

      <SimpleDialog
        selectedValue={selectedValue}
        open={open}
        onClose={handleClose}
      />

      <Grid container item xs={6} spacing={0} className={classes.catalog}>
        <Grid container item direction="column">
          <Typography variant="subtitle2" className={classes.title}>
            Ürün kataloğumuzu indirin
          </Typography>
          <Typography variant="body1" className={classes.body}>
            Her gün 4500’den fazla ürettiğimiz ayakkabı modellerimizi inceleyin.
            İstediğiniz zaman teklif alın
          </Typography>
          <Button
            className={classes.button}
            disableRipple={true}
            style={{ backgroundColor: "#D50000" }}
          >
            Kataloğu İndir
          </Button>
          <img className={classes.img} src={news} />
        </Grid>
      </Grid>
    </Grid>
  );
}
