import { withRouter, useParams, Link } from "react-router-dom";
import makeStyles from "@mui/styles/makeStyles";
import line from "../Components/Footer/img/Line.svg";
import { Grid, Typography } from "@mui/material";

const useStyles = makeStyles((theme) => ({
  productImg: { height: "500px", width: "500px" },
}));

function ProductDetail({ data }) {
  const { id } = useParams();
  const classes = useStyles();

  return (
    <>
      
      {data
        .filter((product) => product.id == id)
        .map((product, i) => (
          <div key={i}>
            <Grid container>
              <Grid item><Typography><Link to='/products' >Urunler</Link></Typography></Grid>
              <Grid item > <img src={line} /> </Grid>
            </Grid>
            




            <h1>{product.id}</h1>
            <h1>{product.name}</h1>
            <h1>{product.description}</h1>
            <img className={classes.productImg} src={product.img} />



          </div>
        ))}
    </>
  );
}

export default withRouter(ProductDetail);
