import * as React from "react";
import { Grid, Typography, Button, Container } from "@mui/material";
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import makeStyles from "@mui/styles/makeStyles";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown } from "@fortawesome/free-solid-svg-icons/faAngleDown";

const useStyles = makeStyles((theme) => ({
  main: {},
  dropdownsGrid: { padding: "0 0 0 120px" },
  dropdownButton: {},
  dropdownText: { fontSize: "18px", textTransform:'none' },
}));

const Products = () => {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  return (
    <>
      <Grid container className={classes.main}>
        <Grid
          container
          item
          xs={11}
          justifyContent="flex-start"
          className={classes.dropdownsGrid}
        >
          {/* MARKA DROPDOWN */}
          <Grid item className={classes.dropdownButton}>
            <Button
              id="basic-button"
              aria-controls="basic-menu"
              aria-haspopup="true"
              aria-expanded={open ? "true" : undefined}
              onClick={handleClick}
              disableRipple={true}
              style={{
                backgroundColor: "transparent",
              }}
            >
              <Typography className={classes.dropdownText} color="primary">
                Marka
              </Typography>
              <FontAwesomeIcon icon={faAngleDown} size="1x" />
            </Button>

            <Menu
              id="basic-menu"
              anchorEl={anchorEl}
              open={open}
              onClose={handleClose}
              MenuListProps={{
                "aria-labelledby": "basic-button",
              }}
            >
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>Marka1</Typography>
              </MenuItem>
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>Marka2</Typography>
              </MenuItem>
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>Marka3</Typography>
              </MenuItem>
            </Menu>
          </Grid>
          {/* CINSIYET DROPDOWN */}
          <Grid item className={classes.dropdownButton}>
            <Button
              id="basic-button"
              aria-controls="basic-menu"
              aria-haspopup="true"
              aria-expanded={open ? "true" : undefined}
              onClick={handleClick}
              disableRipple={true}
              style={{
                backgroundColor: "transparent",
              }}
            >
              <Typography className={classes.dropdownText} color="primary">
                Cinsiyet
              </Typography>
              <FontAwesomeIcon icon={faAngleDown} size="1x" />
            </Button>

            <Menu
              id="basic-menu"
              anchorEl={anchorEl}
              open={open}
              onClose={handleClose}
              MenuListProps={{
                "aria-labelledby": "basic-button",
              }}
            >
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>Kadin</Typography>
              </MenuItem>
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>Erkek</Typography>
              </MenuItem>
            </Menu>
          </Grid>
          {/* URUNLERI SIRALA DROPDOWN */}
          <Grid
            container
            item
            className={classes.dropdownButton}
            xs={9}
            direction="row-reverse"
          >
            <Button
              id="basic-button"
              aria-controls="basic-menu"
              aria-haspopup="true"
              aria-expanded={open ? "true" : undefined}
              onClick={handleClick}
              disableRipple={true}
              style={{
                backgroundColor: "transparent",
              }}
            >
              <Typography className={classes.dropdownText} color="primary">
                Urunleri Sirala
              </Typography>
              <FontAwesomeIcon icon={faAngleDown} size="1x" />
            </Button>

            <Menu
              id="basic-menu"
              anchorEl={anchorEl}
              open={open}
              onClose={handleClose}
              MenuListProps={{
                "aria-labelledby": "basic-button",
              }}
            >
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>Urun1</Typography>
              </MenuItem>
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>URUN2</Typography>
              </MenuItem>
              <MenuItem
                onClick={handleClose}
                style={{ backgroundColor: "transparent" }}
                disableRipple={true}
              >
                <Typography className={classes.dropdownText}>URUN3</Typography>
              </MenuItem>
            </Menu>
          </Grid>
        </Grid>
      </Grid>
    </>
  );
};

export default Products;
