import * as React from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import { Button, Grid } from "@mui/material";
/* import HomePageCardImage from "./HomePageCardImage";
 */ import makeStyles from "@mui/styles/makeStyles";

const useStyles = makeStyles((theme) => ({
  title: {
    position: "relative",
    maxWidth: "80%",
    height: "21px",
    marginLeft: "15px",
    marginTop: "10px",
    lineHeight: "21px",
    fontSize: "18px",
  },
  body: {
    position: "relative",
    maxWidth: "80%",
    height: "17px",
    marginLeft: "15px",
    marginTop: "5px",
    lineHeight: "17px",
    fontSize: "14px",
  },
  button: {
    position: "relative",
    textTransform: "none",
    height: "1%",
    width: "100%",
    marginTop: "10px",
    marginLeft: "0px",
  },
  inspectText: {
    textDecoration: "underline",
    lineHeight: "17px",
    fontSize: "16px",
    marginTop: "55px",
    marginLeft: "0px",
  },
  img: {
    width: "100%",
    height: "100%",
    maxHeight: "110%",
    filter: "grayscale(100%)",
    "&:hover": {
      filter: "grayscale(0%)",
    },
  },
  imgGrid: {},
  container: { height: "100%" },
}));

export default function HomePageCard({
  title,
  body,
  img,
  cardLink,
  width,
  height,
}) {
  const classes = useStyles();
  return (
    <Card sx={{ width: width, height: height }} elevation={1}>
      <Grid container>
        <Grid item>
          <CardContent style={{ padding: 0 }}>
            <Grid container direction="column" className={classes.container}>
              {/* <HomePageCardImage image={img} /> */}
              <Grid item className={classes.imgGrid}>
                <img src={img} className={classes.img} />
              </Grid>

              <Grid container item xs={12} direction="column-reverse">
                <Typography variant="subtitle1" className={classes.title}>
                  {title}
                </Typography>
              </Grid>

              <Grid item xs={12} direction="column-reverse">
                <Typography variant="body1" className={classes.body}>
                  {body}
                </Typography>
              </Grid>
            </Grid>
          </CardContent>
        </Grid>

        <Grid container item xs={12}  direction="column-reverse" >
          <CardActions>
            <Button
              disableRipple={true}
              href={cardLink}
              className={classes.button}
              style={{ background: "red" }}
            >
              <Typography className={classes.inspectText} color="secondary">
                Incele
              </Typography>
            </Button>
          </CardActions>
        </Grid>
      </Grid>
    </Card>
  );
}
