import React, { useState, useRef } from "react";
import makeStyles from "@mui/styles/makeStyles";
import { Grid, Typography, Button, Container } from "@mui/material";
import ReactPlayer from "react-player";
import video from "../Components/About/img/slot.mp4";

/* import IconButton from '@mui/material/IconButton';
import Slider from '@mui/material/Slider';
import Tooltip from '@mui/material/Tooltip';
import Popover from '@mui/material/Popover';
import {
  FastRewindIcon, FastForwardIcon,
  PlayArrowIcon, PauseIcon, FullScreenIcon, VolumeUpIcon
} from '@mui/icons-material'; */
import VideoPlayerControls from "../Components/About/VideoPlayerControls";
// import screenfull from 'screenfull';

const useStyles = makeStyles((theme) => ({
  main: {
    background: "linear-gradient(360deg, #FFF 0%, #F4F5F7 30%)",
    mixBlendMode: "normal",
  },
  about: {
    marginTop: "80px",
  },
  playerWrapper: {
    width: "100%",
    position: "relative",
  },
  playerContainer: {
    marginTop: "50px",
  },
}));

const format = (seconds) => {
  if (isNaN(seconds)) {
    return "00:00";
  }

  const date = new Date(seconds * 1000);
  const hh = date.getUTCHours();
  const mm = date.getUTCMinutes();
  const ss = date.getUTCSeconds().toString().padStart(2, "0");

  if (hh) {
    return `${hh}:${mm.toString().padStart(2, "0")}:${ss}`;
  }

  return `${mm}:${ss}`;
};

let count = 0;

export default function About() {
  const classes = useStyles();
  const [state, setState] = useState({
    playing: true,
    muted: false,
    volume: 0.5,
    playbackRate: 1.0,
    played: 0,
    seeking: false,
  });

  const { playing, muted, volume, playbackRate, played, seeking } = state;

  const handlePlayPause = () => {
    setState({ ...state, playing: !state.playing });
  };

  const playerRef = useRef(null);
  const playerContainerRef = useRef(null);
  const controlsRef = useRef(null);

  const handleRewind = () => {
    playerRef.current.seekTo(playerRef.current.getCurrentTime() - 10);
  };
  const handleForward = () => {
    playerRef.current.seekTo(playerRef.current.getCurrentTime() + 10);
  };
  const handleMute = () => {
    setState({ ...state, muted: !state.muted });
  };
  const handleVolumeChange = (e, newValue) => {
    setState({
      ...state,
      volume: parseFloat(newValue / 100),
      muted: newValue === 0 ? true : false,
    });
  };
  const handleVolumeSeekUp = (e, newValue) => {
    setState({
      ...state,
      volume: parseFloat(newValue / 100),
      muted: newValue === 0 ? true : false,
    });
  };
  const handlePlayBackRateChange = (rate) => {
    setState({ ...state, playbackRate: rate });
  };
  /*const handleToggleFullScreen = () => {
    screenfull.toggle(playerContainerRef.current);
  };*/
  const handleProgress = (changeState) => {
    if (count > 3) {
      controlsRef.current.style.visibility = "hidden";
      count = 0;
    }

    if (controlsRef.current.style.visibility === "visible") {
      count += 1;
    }

    if (!state.seeking) {
      setState({ ...state, ...changeState });
    }
  };

  const handleSeekChange = (e, newValue) => {
    setState({ ...state, played: parseFloat(newValue / 100) });
  };
  const handleSeekMouseDown = (e) => {
    setState({ ...state, seeking: true });
  };
  const handleSeekMouseUp = (e, newValue) => {
    setState({ ...state, seeking: false });
    playerRef.current.seekTo(newValue / 100);
  };

  const handleMouseMove = () => {
    controlsRef.current.style.visibility = "visible";
    count = 0;
  };

  const currentTime = playerRef.current
    ? playerRef.current.getCurrentTime()
    : "00:00";
  const duration = playerRef.current
    ? playerRef.current.getDuration()
    : "00:00";
  const ellapsedTime = format(currentTime);
  const totalDuration = format(duration);

  return (
    <Grid
      className={classes.main}
      container
      direction="column"
      justifyContent="center"
      alignItems="center"
    >
      <Grid
        container
        item
        alignItems="center"
        justifyContent="center"
        spacing={8}
      >
        <Grid item xs={7}>
          <Typography
            variant="subtitle1"
            className={classes.about}
            color="primary"
          >
            Hakkimizda
          </Typography>
        </Grid>

        <Grid item xs={7}>
          <Button>Tarihce</Button>
          <Button>Politikalar</Button>
          <Button>Neden Biz?</Button>
          <Button>Sirket Bilgileri</Button>
        </Grid>
      </Grid>

      <Container className={classes.playerContainer} maxWidth="md">
        <div
          ref={playerContainerRef}
          className={classes.playerWrapper}
          onMouseMove={handleMouseMove}
        >
          <ReactPlayer
            ref={playerRef}
            width={"100%"}
            height={"100%"}
            url={video}
            muted={muted}
            playing={playing}
            volume={volume}
            playbackRate={playbackRate}
            onProgress={handleProgress}
          />

          <VideoPlayerControls
            ref={controlsRef}
            onPlayPause={handlePlayPause}
            playing={playing}
            muted={muted}
            volume={volume}
            onRewind={handleRewind}
            onForward={handleForward}
            onMute={handleMute}
            onVolumeChange={handleVolumeChange}
            onVolumeSeekUp={handleVolumeSeekUp}
            playbackRate={playbackRate}
            onPlayBackRateChange={handlePlayBackRateChange}
            //onToggleFullScreen={handleToggleFullScreen}
            played={played}
            onSeek={handleSeekChange}
            onSeekMouseDown={handleSeekMouseDown}
            onSeekMouseUp={handleSeekMouseUp}
            ellapsedTime={ellapsedTime}
            totalDuration={totalDuration}
          />
        </div>
      </Container>

      <Grid
        container
        item
        direction={"column"}
        className={classes.text}
        justifyContent="flex-start"
        alignItems="center"
      >
        <Grid item>
          <Typography variant="subtitle2">Ortaklar Ayakkabıcılık</Typography>
        </Grid>

        <Grid item>
          <Typography>
            2014 yılından itibaren çocuk ayakkabısı üretim kategorisinde
            Türkiye’nin en kapsamlı AR-GE’si ve üretimine sahiptir.
          </Typography>
        </Grid>
        <Grid item>
          <Typography>
            Her sezon güçlü AR-GE departmanı ile birçok sektöründe lider
            markalara çocuk ayakkabısı tasarlayarak modaya yön vermektedir.
          </Typography>
        </Grid>

        <Grid item>
          <Typography>
            Ortaklar Ayakkabıcılık, yılların vermiş olduğu deneyimleri AR-GE
            gücü ve üretim gücünü birleştirerek üretim kalitesi ile birlikte
            Türkiye ve Avrupa’da çocuk ayakkabı üretiminde lider olmak ve
            ihracat kapasitesini de her geçen yıl arttırarak yeniliklere devam
            edecektir.
          </Typography>
        </Grid>
      </Grid>
    </Grid>
  );
}
