import React, { useState } from "react";
import makeStyles from "@mui/styles/makeStyles";
import { Grid, Typography, Button } from "@mui/material";
import Line from "../Components/About/img/Line.svg";
import AboutPageButton from "../Components/About/AboutPageButton";
import HistoryPage from "../Components/About/history";
// import screenfull from 'screenfull';

const useStyles = makeStyles((theme) => ({
  main: {
    background: "linear-gradient(360deg, #FFF 0%, #F4F5F7 100%)",
    mixBlendMode: "normal",
    maxWidth: "1600px",
    margin: "auto",
  },
  about: {
    marginTop: "80px",
  },
  playerWrapper: {
    width: "100%",
    position: "relative",
  },
  playerContainer: {
    marginTop: "50px",
  },
  text: { width: "50%", marginTop: "50px" },
  redLine: { position: "relative", marginLeft: "52%" },
  picturesAndTexts: { background: "#F4F5F7", transform: 0, marginTop: "50px" },
  circleAndRing1: {
    position: "absolute",
    marginLeft: "3.05%",
    marginTop: "15%",
  },
  circleAndRing2: {
    position: "absolute",
    marginLeft: "3.05%",
    marginTop: "42%",
  },
  circleAndRing3: {
    position: "absolute",
    marginLeft: "3.05%",
    marginTop: "69%",
  },
  circleAndRing4: {
    position: "absolute",
    marginLeft: "3.05%",
    marginTop: "96%",
  },
  circleAndRing5: {
    position: "absolute",
    marginLeft: "3.05%",
    marginTop: "123%",
  },
  imgLeft: { padding: " 0px 0 0 0", height: "300px" },
  textLeft: { padding: " 0px 0 0 0", height: "300px" },
  imgRight: { padding: " 0px 0 0 0", height: "300px" },
  textRight: { padding: " 0px 0 0 0", height: "300px" },
  line: { marginTop: "20px" },
}));

export default function About() {
  const [active, setActive] = useState("tarihcemiz");
  const classes = useStyles();
  return (
    <Grid
      className={classes.main}
      container
      direction="column"
      justifyContent="center"
      alignItems="center"
    >
      <Grid
        container
        item
        alignItems="center"
        justifyContent="center"
        spacing={8}
      >
        <Grid item xs={7}>
          <Typography
            variant="subtitle1"
            className={classes.about}
            color="primary"
          >
            Hakkimizda
          </Typography>
        </Grid>

        <Grid item xs={7}>
          <Button onClick={()=> setActive('tarihcemiz')}>Tarihcemiz</Button>
          <Button onClick={()=> setActive('politikalar')}>Politikalar</Button>
          <Button onClick={()=> setActive('nedenbiz')}>Neden Biz?</Button>
          <Button onClick={()=> setActive('sirketbilgileri')}>Sirket Bilgileri</Button>
        </Grid>
      </Grid>

      <img src={Line} className={classes.line} />
      
      {active === 'tarihcemiz' &&  <HistoryPage /> }
      

    </Grid>
  );
}
